use crate::jubjub::{edwards, JubjubEngine, PrimeOrder};

use ff::PrimeField;

use crate::constants;
use blake2s_simd::Params;

/// Produces a random point in the Jubjub curve.
/// The point is guaranteed to be prime order
/// and not the identity.
pub fn group_hash<E: JubjubEngine>(
    tag: &[u8],
    personalization: &[u8],
    params: &E::Params,
) -> Option<edwards::Point<E, PrimeOrder>> {
    assert_eq!(personalization.len(), 8);

    // Check to see that scalar field is 255 bits
    assert!(E::Fr::NUM_BITS == 255);

    let h = Params::new()
        .hash_length(32)
        .personal(personalization)
        .to_state()
        .update(constants::GH_FIRST_BLOCK)
        .update(tag)
        .finalize();

    match edwards::Point::<E, _>::read(h.as_ref(), params) {
        Ok(p) => {
            let p = p.mul_by_cofactor(params);

            if p != edwards::Point::zero() {
                Some(p)
            } else {
                None
            }
        }
        Err(_) => None,
    }
}

#[cfg(test)]
mod test {
    use ff::PrimeField;
    use group_hash::group_hash;
    use pairing::bls12_381::{Bls12, Fr};

    use crate::jubjub::{edwards, JubjubBls12, JubjubEngine, PrimeOrder};

    #[test]
    fn test_group_hash() {
        fn find_group_hash<E: JubjubEngine>(
            m: &[u8],
            personalization: &[u8; 8],
            params: &E::Params,
        ) -> edwards::Point<E, PrimeOrder> {
            let mut tag = m.to_vec();
            let i = tag.len();
            tag.push(0u8);

            loop {
                let gh = group_hash(&tag, personalization, params);

                // We don't want to overflow and start reusing generators
                assert!(tag[i] != u8::max_value());
                tag[i] += 1;

                if let Some(gh) = gh {
                    break gh;
                }
            }
        }
        let params = JubjubBls12::new();

        let expected_points: Vec<edwards::Point<Bls12, PrimeOrder>> = vec![];
        let domains = vec![
            hex!("0b4c693149060f99"),
            hex!("982fc029ed2213e4"),
            hex!("5a01101e28c6b466"),
            hex!("a3716d31a988b6e6"),
        ];
        let msgs = vec![
            hex!(""),
            hex!(""),
            hex!(""),
            hex!("")
        ];

        let ys = vec![
            "8502599294297669157183582041043506286304348771153601905088214968423735432772",
            "33965310400650966081486833884535323100804531882948083108992748314044766607474",
            "15277426621450144245366093477629790944965634885834431068514786570163432982421",
            "35526445498940553839675656924597924255939683458731864358252626115877434851278",
        ];
        let xs = vec![
            "23479585783156774250942425515624703792585157520679515316930097097463607664576",
            "30414851484511157010605445406157992259368652076831836832380699127755424334026",
            "34566775937206506013251574661622220967552701387632591444790779184716709173668",
            "32625571922270028001313966220069858825087579007581150636305043327525524456655",
        ];
        for i in 0..domains.len() {
            let domain = domains[i];
            let msg = msgs[i];

            let gh: edwards::Point<Bls12, _> = find_group_hash(&msg, &domain, &params);

            let p_sign_false =
                edwards::Point::<Bls12, _>::get_for_y(Fr::from_str(ys[i]).unwrap(), false, &params)
                    .unwrap();
            let p_sign_true =
                edwards::Point::<Bls12, _>::get_for_y(Fr::from_str(ys[i]).unwrap(), true, &params)
                    .unwrap();
            let is_one_of_xs = p_sign_false.into_xy().0 == Fr::from_str(xs[i]).unwrap()
                || p_sign_true.into_xy().0 == Fr::from_str(xs[i]).unwrap();
            let is_y = p_sign_false.into_xy().1 == Fr::from_str(ys[i]).unwrap()
                && p_sign_true.into_xy().1 == Fr::from_str(ys[i]).unwrap();
            assert!(is_one_of_xs && is_y);
        }

        for m in 0..5 {
            use byteorder::{LittleEndian, WriteBytesExt};
            let mut segment_number = [0u8; 4];
            (&mut segment_number[0..4])
                .write_u32::<LittleEndian>(m)
                .unwrap();
            let p: edwards::Point<Bls12, _> =
                find_group_hash(&segment_number, b"Zcash_PH", &params);
        }
    }
}
